ampl = 0.25fs = 44100;nsec = 2;#freq = 440;nsample = fs*nsec;time = linspace(0,nsec,nsample);freq_array_ref = [38,     55,   77.7,   110,    155.6,    220,    311.1,    440,    622.3,    880];freq_array_sim_10= [38.877E+0,54.944E+0,77.65E+0,109.74E+0,155.092E+0,219.185E+0,309.767E+0,437.782E+0,618.702E+0,874.389E+0];freq_array_sim_45= [38.944E+0,55.131E+0,78.048E+0,110.491E+0,156.419E+0,221.439E+0,313.485E+0,443.793E+0,628.267E+0,889.423E+0];for idx  = 1: length(freq_array_ref)  % reference    %create wave signal  printf("freq_array_ref ---> %d Hz \n",freq_array_ref(idx));  wave = ampl * sin(2*pi*freq_array_ref(idx)*time);  player = audioplayer (wave , 44100, 8);  %play  play (player);  while isplaying(player)    pause(.1);  endwhile  % sim 10 C  %create wave signal  printf("freq_array_sim_10\n");  wave = ampl * sin(2*pi*freq_array_sim_10(idx)*time);  player = audioplayer (wave , 44100, 8);   %play  play (player);  while isplaying(player)    pause(.1);  endwhile      % sim 45 C  %create wave signal  printf("freq_array_sim_45\n");  wave = ampl * sin(2*pi*freq_array_sim_45(idx)*time);  player = audioplayer (wave , 44100, 8);  %play  play (player);  while isplaying(player)    pause(.1);  endwhile  end